mapper.update:
update breply
set content = #{content}
where rnum = #{rnum}

mapper.select:
select rnum,
content, to_char(regdate,'yyyy-mm-dd') regdate, id, num, r
	FROM(
	select rnum, content, regdate, id, num, rownum r
		FROM(
		select rnum, content, regdate, id, num
			from breply
			where num = #{num}
			order by rnum DESC
			)
		
)WHERE r >= #{sno} and r <= #{eno}
