mapper.update:
update gallery        
set viewcnt = viewcnt + 1
where gno = #{gno}

update gallery
set pw = #{newpw}
where gno = #{gno}

mapper.select:
select count(gno)
from gallery        
where gno=#{gno} and pw=#{pw}

mapper.update:
UPDATE gallery                      
SET title=#{title}, content=#{content}, fname=#{fname}, writer=#{writer}
WHERE gno = #{gno}
